// Obtener datos del webhook
const items = $input.all();
const data = items[0]?.json || {};

const tasks = data.body?.tasks || data.tasks || [];
const boardTitle = data.body?.boardTitle || data.boardTitle || 'Kanban Board';

if (tasks.length === 0) {
  throw new Error('No hay tareas para exportar');
}

// Headers del CSV
const headers = ['ID', 'Título', 'Descripción', 'Columna', 'Prioridad', 'Fecha Límite', 'Progreso (%)', 'Fecha Creación'];

// Convertir tasks a filas CSV
const rows = tasks.map(task => {
  return [
    task.id || '',
    `"${(task.title || '').replace(/"/g, '""')}"`,
    `"${(task.description || '').replace(/"/g, '""').replace(/\n/g, ' ')}"`,
    task.column || '',
    task.priority || '',
    task.dueDate ? new Date(task.dueDate).toLocaleDateString('es-AR') : '',
    task.progress || 0,
    task.createdAt ? new Date(task.createdAt).toLocaleDateString('es-AR') : ''
  ].join(',');
});

// Construir CSV
const csvContent = [headers.join(','), ...rows].join('\n');
const fileName = `kanban-backlog-${new Date().toISOString().split('T')[0]}.csv`;

// Convertir a binary
const buffer = Buffer.from(csvContent, 'utf-8');

return {
  json: {
    boardTitle: boardTitle,
    totalTasks: tasks.length,
  },
  binary: {
    attachment: {
      data: buffer.toString('base64'),
      mimeType: 'text/csv',
      fileName: fileName,
      fileExtension: 'csv'
    }
  }
};
